BEGIN TRANSACTION;
CREATE TABLE "pays" (
	`id`	INTEGER NOT NULL DEFAULT '0' PRIMARY KEY AUTOINCREMENT,
	`ISO`	varchar(3) NOT NULL DEFAULT '' UNIQUE,
	`nom`	varchar(255) NOT NULL,
	`nom2`	varchar(255) NOT NULL DEFAULT '',
	`continent`	varchar(20),
	`capitale`	varchar(255) NOT NULL DEFAULT '',
	`population`	bigint(20) NOT NULL DEFAULT 0,
	`nomHabitants`	varchar(255) NOT NULL DEFAULT '',
	`superficie`	float NOT NULL DEFAULT 0,
	`densite`	float,
	`popUrbaine`	tinyint(4),
	`frontieres`	int(11),
	`cotes`	int(11),
	`eauxTerritoriales`	int(11),
	`heure`	varchar(255),
	`moisFroids`	int(11),
	`moisFroidsTemp`	int(11),
	`moisChaud`	int(11),
	`moisChaudsTemp`	int(11)
);
COMMIT;
